import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators} from '@angular/forms';

@Component({
    selector: 'app-crear-producto',
    templateUrl: './registro-usuario.component.html',
    styleUrls: ['./registro-usuario.component.css']
})
export class CrearusuarioComponent {
    usuarioForm:FormGroup
    regexString = /^[a-zA-Z0-9\s]+$/
    regexNumero = /^[0-9]+$/

    constructor(private fb:FormBuilder){
        this.usuarioForm = this.fb.group({
            nombre: ['', [Validators.required, Validators.pattern(this.regexString)]],
            edad_usuario: ['', [Validators.required, Validators.pattern(this.regexNumero), Validators.min(1)]],
            documento:['', [Validators.required, Validators.pattern(this.regexNumero), Validators.minLength(6)]],
        })
    }

    actualizar_registrar_huesped(){
        console.log(this.usuarioForm)
    }
}
