import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';

@Component({
    selector: 'app-inicia-sesion',
    templateUrl: './inicia-sesion.component.html',
    styleUrls: ['./inicia-sesion.component.css']
})
export class IniciaSesionComponent {

    inicioForm:FormGroup

    constructor(private fb:FormBuilder){
        this.inicioForm = this.fb.group({
            nombre:['',[Validators.required]],
            contraseña:['',[Validators.required]]
        })
    }

    inicioUsuario(){
        console.log(this.inicioForm)
    }

}
<form [formGroup]="inicioUsuario" (ngSubmit)="inicioUsuario">
    <div class="caja">

        <h1 class="titulo">Inicia sesión</h1>
    <label>
        <i class="fa-solid fa-user"></i>
        <input placeholder="Ingresa tu usuario" type="text" id="user">
        <div class="text-black fw-bold fs-6" *ngIf="inicioUsuario.get('nombre')?.hasError('required') && inicioUsuario.get('nombre')?.touched" >Nombre de usuario obligatorio</div>
    </label>
    <label>
        <i class="fa-solid fa-lock"></i>
        <input placeholder="Ingresa tu contraseña" type="password" id="password">
        <div class="text-black fw-bold fs-6" >contraseña obligatoria</div>
    </label>
    <button>
        <span class="text">Iniciar sesión</span>
    </button>
    </div>

</form>
